import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import { TermContext, TypeContext } from './utils/SearchContext'
import { Navbar } from "./components/Navbar"
import { Search } from "./pages/Search"
import { Bookshelf } from "./pages/Bookshelf"
import { Finished } from "./pages/Finished"

function App() {

  const [state, setState] = useState ({
    term: "",
    type: ""
  })

  const [searchTerm, setSearchTerm] = useState({
    term: "",
    handleInputChange: (event) => {
      const { value } = event.target
      setSearchTerm({ ...searchTerm, term: value })
      setState({ ...searchTerm, term: value })
    },
    handleSubmit: (event) => {
      event.preventDefault()
      console.log(state)
    }
  })

  const [searchType, setSearchType] = useState({
    type: "",
    handleSelectorChange: (event) => {
      const type = event.target.textContent
      setSearchType({ ...searchType, type: type })
    }
  })

  return (
    <Router>
      <Navbar />
      <TermContext.Provider value={searchTerm}>
        <TypeContext.Provider value={searchType}>
          <Switch>
            <Route exact path="/bookshelf">
              <Bookshelf />
            </Route>
            <Route exact path="/read">
              <Finished />
            </Route>
            <Route exact path="/search">
              <Search />
            </Route>
          </Switch>
      </TypeContext.Provider>
      </TermContext.Provider>
    </Router>
      );
    }

    export default App;
