import React, { useState } from 'react'
import { Button, Select, Input } from 'semantic-ui-react'

export const SearchForm = () => {

    const [input, setInput] = useState({
        type: "title",
        term: ""
    });

    const options = [
        { key: 'title', data: 'title', text: 'Title', value: 'title' },
        { key: 'author', data: 'author', text: 'Author', value: 'author' },
        { key: 'genre', data: 'genre', text: 'Genre', value: 'genre' },
    ]

    const handleInputChange = (event) => {
        console.log(event.target)
    }

    const handleSelectorChange = (event) => {
        const dropdown = (document.getElementByClassName("menu"))
        for (const option of dropdown) {
            console.log(option)
        }
        // setInput( {...input, type: document.getElementById("selectBtn").children[0].textContent.toLowerCase() })
        // return (console.log(input))

    }

    const handleSubmit = (event) => {
        event.preventDefault()
        console.log (document.getElementById("selectBtn").children[0].textContent.toLowerCase())
        console.log(event.target)
    }

    return (
        <div style={{ textAlign: "center", margin: 50 }}>
            <form onSubmit={handleSubmit} >
                <Input type='text' placeholder='Search...' action>
                    <input onChange={handleInputChange} />
                    <Select onChange={handleSelectorChange} compact options={options} defaultValue='title' id="selectBtn" />
                    <Button type='submit'>Search</Button>
                </Input>
            </form>
        </div>
    )
}
