import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import { SearchContext } from './utils/SearchContext'
import API from './utils/API'
import { Navbar } from "./components/Navbar"
import { Search } from "./pages/Search"
import { Bookshelf } from "./pages/Bookshelf"
import { Finished } from "./pages/Finished"

function App() {
  const [search, updateSearch] = useState({
    term: "",
    type: "Title",
    results: []
  })

  const handleInputChange = ((event) => {
    const { value } = event.target
    updateSearch({ ...search, term: value })
  })

  const handleSubmit = (event => {
    event.preventDefault()
    googleSearch(search)
  })

  const handleSelectorChange = (event => {
    const type = event.target.textContent
    updateSearch({ ...search, type: type })
  })

  const googleSearch = (query => {
    API.search(query)
      .then(res => {
        const results = res.data.items.map(item => [{
          title: item.volumeInfo.title,
          authors: item.volumeInfo.authors,
          pages: item.volumeInfo.pageCount,
          image: item.volumeInfo.imageLinks.smallThumbnail,
          summary: item.volumeInfo.description,
          categories: item.volumeInfo.categories
        }])
        console.log(results)
        // updateSearch({ ...search, results: res.data })
      })
  })

  return (
    <Router>
      <Navbar />
      <SearchContext.Provider value={{search, handleInputChange, handleSubmit, handleSelectorChange, googleSearch} }>
        <Switch>
          <Route exact path="/bookshelf">
            <Bookshelf />
          </Route>
          <Route exact path="/read">
            <Finished />
          </Route>
          <Route exact path="/search">
            <Search />
          </Route>
        </Switch>
      </SearchContext.Provider>
    </Router>
  );
}

export default App;
